CREATE TABLE BOOK (ID INTEGER GENERATED BY DEFAULT AS IDENTITY NOT NULL, PAGES INTEGER, PRICE DECIMAL(10,2), PUBLISHEDAT TIMESTAMP, STOCKSTATUS VARCHAR(20), TITLE VARCHAR(255) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE AUTHOR (ID INTEGER GENERATED BY DEFAULT AS IDENTITY NOT NULL, BIRTHDATE TIMESTAMP, NAME VARCHAR(255) NOT NULL, PRIMARY KEY (ID))
CREATE TABLE book_author (book_id INTEGER NOT NULL, author_id INTEGER NOT NULL, PRIMARY KEY (book_id, author_id))
ALTER TABLE book_author ADD CONSTRAINT bookauthorauthorid FOREIGN KEY (author_id) REFERENCES AUTHOR (ID)
ALTER TABLE book_author ADD CONSTRAINT book_authorbook_id FOREIGN KEY (book_id) REFERENCES BOOK (ID)
